import React, { Component } from 'react';

export default class Users extends Component {
  //Estado para monitorar tempo de visibilidade do component
  constructor() {
    super();

    this.state = {
      secondsVisible: 0,
    };

    this.interval = null;
  }

  /*
    METODOS DE CICLOS DE VIDA
    componentDidMount, 
    componentDidUpdate, 
    componentWillUnmount
  */ 
  async componentDidMount() {
    console.log('componentDidUpdate de Users.js');

    this.interval = setInterval(() => {
      const { secondsVisible } = this.state;

      this.setState({
        secondsVisible: secondsVisible + 1,
      })
    }, 1000);
  }

  componentDidUpdate() {
    console.log('componentDidUpdate de Users.js');
  }

  componentWillUnmount() {
    //parando para nao consumir memoria quando botao ta desabilitado
    clearInterval(this.interval);
  }


  render() {
    //desestruturando a props
    const { users } = this.props;

    //desestruturando o state
    const { secondsVisible } = this.state;

    return (
      <div>
        <p>Componente Users visivel por {secondsVisible} segundos</p>
        <ul>
          {users.map((user) => {
            //desestruturando
            const { login, name, picture } = user;

            return <p key={login.uuid}>{name.first}</p>;
          })}
        </ul>
      </div>
    )
  }
}
